import React, { Component } from "react";
import Chat from '../components/Chat.js';


class ChatWindow extends Component {

  renderChat = () => {
    return this.props.chats.map(chat => <Chat chat={chat}  key={chat.id}/>)
  }


  render() {
    return (
      <div >
        {this.renderChat()}

      </div>
    )
  }
}

export default ChatWindow;
