import React, { Component } from "react";


class Chat extends Component {

  handleClick = (e) => {

    fetch(`http://localhost:3000/chats/${this.props.chat.id}`, {
      method:'DELETE',
      headers: { 
        'content-type': 'application/json',
        'accept': 'application/json'
      }
    })
      .then(r => r.json())
      .then(deletedChat => console.log(deletedChat.data.id))
      .catch(err => console.log(err))

  }

  render() {
    console.log(this.props)
    return (
      <div className='chat'>
        {this.props.name}: {this.props.message} {<button onClick={(e) => this.handleClick(e)}>Delete</button>}
      </div>
    )
  }
}

export default Chat;


